        var myDiv = document.getElementById(""+this.refs.search_glass);
        ReactDom.findDOMNode(myDiv).style.background = 'green';
    }
    componentDidMount() {
        var myDiv = document.getElementById("search_glass");
            myDiv.addEventListener('click',function () {
                alert()
            });

    }
    forceUpdate(callBack) {
        super.forceUpdate(callBack);
    }

    setStateHandler(){
        alert("Eloo")
        let model=new Model();
        let view=new View();
        let control=new Control(view,model);
        let tableData=new TableData();
        view.state.printMovieVegasDetails(this.state.rating);
        control.updateView();
        alert()
        ReactDom.findDOMNode("search_glass").addEventListener('click',function () {
           alert()
        });

    }


    render() {

        return  (<div><View/></div>);

    }



}

    ReactDom.render(<Main/>,document.getElementById("root"));
