class View{



    /**
     * model for Movie-vegas
     * declare class variables/attributes as private (local var and Accessor as a object literal);
     * @type {Object}
     */





    printMovieVegasDetails(movieTitle,realeaseDate,story) {

                          let stop=0;
                          let timer;
                   return<div id="re"> {

                       timer = setInterval(function () {

                           stop++;
                           if (stop === 1) {
                               clearInterval(timer);

                               let component=
                                   <div  id='scroll_list'>
                                   <table  id='items'><tr><th >
                                   <img  style='float:left' src='or.jpg'/>
                                   <div id='movie_title'><span id='rating_percentage'> 86</span>
                               <span class='movie_title'>Avengers</span></div>
                               <div id='movie_info'> After the devastating events of Avengers: Infinity War,\n

                                   the universe is in ruins due to the efforts of the Mad Titan,
                                   Thanos. With the help of remaining allies, the Avengers must assemble once more in order to undo Thanos
                                   actions and
                                   restore order to the universe once and for all, no matter what consequences may be in store.
                               </div>

                               <div id='emotional'>
                                   <span class='feelings'>&star;</span><span>  Rating</span>
                               <span class='feelings'>&heartsuit;</span>
                               <span>  Favorite</span><span class='feelings'>&#9016;</span>
                               <span>  Add to list</span><span class='feelings'>&#9003;</span>
                               <span>  Remove</span>
                           </div>
                           </th>


                           </tr>
                           <tr>

                               <td width='400'>

                                   </td>


                           </tr>

                           </table>
                           </div>;
                               return <div>{document.getElementById("result").innerHTML += component} </div>

                           }


                       }, 1000)}
                       <div id="result"> </div>
                   </div>




               }




}



export  default View;
