     */





    printMovieVegasDetails(movieTitle,realeaseDate,story,) {

                          let stop=0;
                          let timer;
                   return(




                          <div id="re"> {

                              timer=setInterval(function () {

                              stop++;
                              if(stop===1){
                                  clearInterval(timer)
                                  let rating ="<div class='items'><div><div class='rating' ><span>&star; </span></div>Your Rating";
                                  let favorite="<div><div class='rating' ><span>&star; </span></div>Your Rating";
                                  let  addToList="<div class='rating' ><span>&star; </span></div>Your Rating";
                                  let  remove= "<div class='rating' ><span>&star; </span></div>Your Rating"
                                  let component=rating+favorite+addToList+remove;
                                  return <div>{document.getElementById("result").innerHTML+=component} </div>

                              }




                          },1000)}  <div id="result"> </div></div>
    )




               }




}



export  default View;
