import Control from "./control";
import Model from "./model";
import View from "./view";
import TableData from "./test";
import React from "react";
import ReactDom from "react-dom"



class Main extends React.Component{



    /**
     *
     * @type {Model}
     */
    constructor(props){
        super(props);
        this.state={
                  rating:"Ratter",
                  data:[]

             };

            this.searchHandler=this.searchHandler.bind(this);
            this.findDomNodeHandler = this.findDomNodeHandler.bind(this);
        }

    findDomNodeHandler() {
        var myDiv = document.getElementById('myDiv');
        ReactDom.findDOMNode(myDiv).style.color = 'green';
    }

    searchHandler(){
        alert("Eloo")
        let model=new Model();
        let view=new View();
        let control=new Control(view,model);
        let tableData=new TableData();
        view.state.printMovieVegasDetails(this.state.rating);
        control.updateView();
        alert()
        ReactDom.findDOMNode("search_glass").addEventListener(function () {
           alert()
        });

    }


    render() {
        var myDiv = document.getElementById('search_glass');

        return  (<div><View/>{ ReactDom.findDOMNode(myDiv)}</div>);
    }



}

    ReactDom.render(<Main/>,document.getElementById("root"));
