import React from "react";
import movie_vegas from  "./movie_vegas.css"
import search_glass from "./search_glass.png"

class View  extends React.Component{

    /**
     * model for Movie-vegas
     * declare class variables/attributes as private (local var and Accessor as a object literal);
     * @type {Object}
     */

    
    setView(rating, likes, goodMovie, badMovie, result, favourites, movieList, watchLater) {


        return (

            <form encType="">


                <div>

                    <div id="heading_background">
                        <div id="menus">
                            <span id="accents">=</span>
                        </div>
                        <div id="search_box">
                            <div className="search_box" placeholder="Search...">
                                <input type="search" id="search_text_field"/>
                                <img id='search_glass' src={search_glass} alt="search"/>
                            </div>
                        </div>

                        <div id="menus_list">
                            <span className="header">Favorites</span>
                            <span className="header">Lists</span>
                            <span className="header">Watch Later</span>
                            <span className="header">Likes</span>
                        </div>

                    </div>
                </div>

                <table>


                </table>
                <div id='scroll_list'>
                    <table>
                        <tbody>
                        <tr id="objects_entry_point">

                            <td className="objects_entry_point">
                                <div id="items"></div>
                            </td>


                        </tr>
                        </tbody>
                    </table>

                </div>
                <footer id="footer"> Dan Technologies Â© Inc {new Date().getFullYear()} </footer>
            </form>
        )
    }



    constructor() {
        super();
        this.state = {

            data:
                [
                    {
                        "id":4,
                        "name":"Foo",
                        "age":"20"
                    },
                    {
                        "id":2,
                        "name":"Bar",
                        "age":"30"
                    },
                    {
                        "id":3,
                        "name":"Baz",
                        "age":"40"
                    }
                ]
        }
    }

    render() {
        return (
            <div>
                <div id="d" ref="d"></div>
                <Header/>
                <table>
                    <tbody>

                    {this.state.data.map((person, i) => <TableRow key = {i}
                                                                  data = {person} />)}
                    </tbody>
                </table>
            </div>
        );
    }

    stage={





    }


}


class Header extends React.Component {

    render() {

        return (
            <div>
                <h1>Header</h1>
            </div>
        );
    }
}
class TableRow extends React.Component {
    render() {
        return (
            <tr>
                <td>{this.props.data.id}</td>
                <td>{this.props.data.name}</td>
                <td>{this.props.data.age}</td>
            </tr>
        );
    }
}


export  default View;
